'use strict';

JQX.Utilities.Assign('Grid.Group', function () {
    function Group() {
        babelHelpers.classCallCheck(this, Group);
    }

    babelHelpers.createClass(Group, [{
        key: 'addGroup',
        value: function addGroup(dataField) {
            var that = this;

            if (!that.dataSource || !that.grouping.enabled) {
                return;
            }

            var index = that.dataSource.groupBy.indexOf(dataField);

            if (index === -1) {
                that.dataSource.groupBy.push(dataField);

                that.refresh(true);
                that.refreshFilters();
            }
        }
    }, {
        key: 'removeGroup',
        value: function removeGroup(dataField) {
            var that = this;

            if (!that.dataSource || !that.grouping.enabled) {
                return;
            }

            var index = that.dataSource.groupBy.indexOf(dataField);

            if (index >= 0) {
                that.dataSource.groupBy.splice(index, 1);

                that.refresh(true);
                that.refreshFilters();
            }
        }
    }, {
        key: 'clearGroups',
        value: function clearGroups() {
            var that = this;

            if (!that.dataSource || !that.grouping.enabled) {
                return;
            }

            that.dataSource.clearGroup();

            that.refresh(true);
            that.refreshFilters();
        }
    }]);
    return Group;
}());