'use strict';

JQX.Utilities.Assign('Grid.Tree', function () {
    function Tree() {
        babelHelpers.classCallCheck(this, Tree);
    }

    babelHelpers.createClass(Tree, [{
        key: 'expandRow',
        value: function expandRow(id) {
            var that = this;

            var row = that.rowById[id];

            if (!row) {
                return;
            }

            row.expanded = true;
        }
    }, {
        key: 'collapseRow',
        value: function collapseRow(id) {
            var that = this;

            var row = that.rowById[id];

            if (!row) {
                return;
            }

            row.expanded = false;
        }
    }]);
    return Tree;
}());