JQX.Utilities.Assign('Grid.Tree', class Tree {
    expandRow(id) {
        const that = this;

        const row = that.rowById[id];

        if (!row) {
            return;
        }

        row.expanded = true;
    }

    collapseRow(id) {
        const that = this;

        const row = that.rowById[id];

        if (!row) {
            return;
        }

        row.expanded = false;
    }
});
