import { storiesOf } from '@storybook/html';
import '../source/styles/jqx.alldefault.js';
import '@ni-kismet/jqx-element/source/jqxelement';
import '../source/jqxcard';

storiesOf('Card', module)
    .add('Basic', () => `
        <jqx-card>
          <h2 class="card-title">Card title</h2>
          <div class="card-content">Card content</div>
        </jqx-card>`)
    .add('With template', () => `
        <jqx-card item-template="customTemplate" data-source='{"title" : "Card title", "content" : "Templated card content"}'></jqx-card>
        <template id="customTemplate">
          <h2 class="card-title">{{title}}</h2>
          <div class="card-content">{{content}}</div>
        </template>`);